import React, { useState, useEffect } from 'react'


const Products = () => {

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState(data);
    const [loading, setloading] = useState(false);
    let componentMounted = true;


    useEffect(() => {
        const getProducts = async () => {
            setloading(true);
            const response = await fetch("https://fakestoreapi.com/products");
            if (componentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setloading(false);
                console.log(filter);
            }
            return () => {
                componentMounted = false;
            }
        }
        getProducts();
    }, []);

    const Loading = () => {
        return (
            <>
                Carregando...
            </>
        )
    }

    const ShowProducts = () => {
        return (
            <>
                <div className="buttons d-flex justify-content-center mb-5 pb-5">
                    <button className="btn btn-outline-dark me-2">Todos</button>
                    <button className="btn btn-outline-dark me-2">Roupas Masculinas</button>
                    <button className="btn btn-outline-dark me-2">Roupas Femininas</button>
                    <button className="btn btn-outline-dark me-2">Joalheria</button>
                    <button className="btn btn-outline-dark me-2">Eletrônicos</button>
                </div>
                {filter.map((product) => {
                    return (
                        <>
                            <div className="col-md-3">
                                <div class="card h-100 text-center p-4" key={product.id} >
                                    <img src={product.image} class="card-img-top" alt={product.title}/>
                                    <div class="card-body">
                                        <h5 class="card-title mb-0">{product.title.substring(0,12)}</h5>
                                        <p class="card-text">
                                            ${product.price}
                                        </p>
                                        <a href="#" className='btn btn-primary'>
                                            Go Somewhere
                                        </a>
                                    </div>
                                </div>
                            </div>
                        </>
                    )
                })}
            </>
        )

    }

    return (
                <div>
                    <div className="container my-5 py-5">
                        <div className="row">
                            <div className="col-12 mb-5">
                                <h1 className='display-6 fw-bolder text-center'>
                                    Últimos Produtos
                                </h1>
                                <hr />
                            </div>
                        </div>
                        <div className="row justify-content-center">
                            {loading ? <Loading /> : <ShowProducts />}
                        </div>
                    </div>
                </div>
                )
}

                export default Products;
